mapper.update:
update
ver = #{ver}+1, name = #{name,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, parent_id = #{parent.id,jdbcType=VARCHAR}, number = #{number,jdbcType=VARCHAR}, is_leaf = #{isLeaf,jdbcType=VARCHAR}, lvl =#{lvl,jdbcType=INTEGER}, target_name = #{targetName,jdbcType=VARCHAR}
where ID = #{id} and ver = #{ver}

mapper.select:
select d.id,d.ver,d.status,d.creater, d.create_time as "createTime", d.editor, d.edit_time as "editTime", d.remark, d.name as "name", d.url as "url", d.parent_id as "parent.id", parent.name as "parent.name", d.number as "number", d.is_leaf as "isLeaf", d.lvl as "lvl", d.target_name as "targetName" from
d left join
parent on d.parent_id = parent.id where d.id = #{id}
